# Make the script compatible with python 3.x
from __future__ import absolute_import, division, print_function, unicode_literals

def count_floor(input):
    floor = 0
    for ch in input:
        if ch == '(':
            floor += 1
        elif ch == ')':
            floor -= 1
        else:
            print("Unknown character: " + ch)

    return floor

def find_basement(input):
    floor = 0
    count = 1
    for ch in input:
        if ch == '(':
            floor += 1
        elif ch == ')':
            floor -= 1
        else:
            print("Unknown character: " + ch)

        if floor == -1:
            return count

        count += 1

    return 0


